mapper.update:
update delivery_order
code=#{code,jdbcType=VARCHAR}, user_account=#{userAccount,jdbcType=VARCHAR}, user_name=#{userName,jdbcType=VARCHAR}, description=#{description,jdbcType=VARCHAR}, status=#{status,jdbcType=TINYINT}, update_user=#{updateUser,jdbcType=VARCHAR}, update_time=#{updateTime,jdbcType=TIMESTAMP}, version=#{version,jdbcType=INTEGER}+1,
WHERE is_delete = 0 and id = #{id,jdbcType=BIGINT};

mapper.select:
select

from delivery_order
where is_delete =0

    and code=#{code,jdbcType=VARCHAR}


    and user_account=#{userAccount,jdbcType=VARCHAR}


    and user_name=#{userName,jdbcType=VARCHAR}


    and  status=#{status,jdbcType=TINYINT}
